  public function hide_or_not($hide_strings,$hide_string) {
    if (strpos($hide_string,'@') === FALSE) {
      if (strpos($hide_strings, '@' . $hide_string . '@') === FALSE) {
        $style = '';
      }
      else {
        $style = 'style="display:none"';
      }
    }
    else {
      if (strpos($hide_strings, $hide_string) === FALSE) {
        $style = '';
      }
      else {
        $style = 'style="display:none"';
      }
    }
    return $style;
